  // Componente Database con filtri
  function Database() {
    const [filters, setFilters] = useState({
      search: '',
      role: '',
      minPotential: '',
      maxAge: '',
      nationality: '',
      foot: '',
      team: '',
      priority: ''
    });
    const [sortBy, setSortBy] = useState('name');
    const [sortOrder, setSortOrder] = useState('asc');
    const [showFilters, setShowFilters] = useState(true);

    // Filtra giocatori
    const filteredPlayers = useMemo(() => {
      let result = [...players];

      // Applica filtri
      if (filters.search) {
        result = result.filter(p => 
          p.name.toLowerCase().includes(filters.search.toLowerCase()) ||
          p.team.toLowerCase().includes(filters.search.toLowerCase()) ||
          p.nationality.toLowerCase().includes(filters.search.toLowerCase())
        );
      }
      if (filters.role) {
        result = result.filter(p => p.general_role === filters.role);
      }
      if (filters.minPotential) {
        result = result.filter(p => (p.potential_value || 0) >= parseInt(filters.minPotential));
      }

      // Ordina
      result.sort((a, b) => {
        let aVal = a[sortBy];
        let bVal = b[sortBy];

        if (typeof aVal === 'string') {
          aVal = aVal.toLowerCase();
          bVal = bVal.toLowerCase();
        }

        if (sortOrder === 'asc') {
          return aVal > bVal ? 1 : -1;
        } else {
          return aVal < bVal ? 1 : -1;
        }
      });

      return result;
    }, [players, filters, sortBy, sortOrder]);

    return (
      <div className="fade-in space-y-6">
        <div className="bg-white rounded-xl p-6 shadow-lg">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold gradient-text">Database Giocatori</h2>
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
            >
              <i className="fas fa-filter mr-2"></i>
              {showFilters ? 'Nascondi Filtri' : 'Mostra Filtri'}
            </button>
          </div>

          {/* Filtri */}
          {showFilters && (
            <div className="bg-gray-50 rounded-lg p-4 mb-6">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <input
                  type="text"
                  placeholder="🔍 Cerca per nome, squadra..."
                  value={filters.search}
                  onChange={(e) => setFilters({...filters, search: e.target.value})}
                  className="field-focus px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
                />

                <select
                  value={filters.role}
                  onChange={(e) => setFilters({...filters, role: e.target.value})}
                  className="field-focus px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
                >
                  <option value="">Tutti i ruoli</option>
                  <option value="Portiere">Portiere</option>
                  <option value="Difensore">Difensore</option>
                  <option value="Centrocampo">Centrocampo</option>
                  <option value="Attaccante">Attaccante</option>
                </select>

                <select
                  value={filters.minPotential}
                  onChange={(e) => setFilters({...filters, minPotential: e.target.value})}
                  className="field-focus px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
                >
                  <option value="">Potenziale minimo</option>
                  <option value="1">1+ stelle</option>
                  <option value="2">2+ stelle</option>
                  <option value="3">3+ stelle</option>
                  <option value="4">4+ stelle</option>
                  <option value="5">5 stelle</option>
                </select>

                <div className="flex space-x-2">
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="field-focus px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
                  >
                    <option value="name">Nome</option>
                    <option value="potential_value">Potenziale</option>
                    <option value="birth_year">Età</option>
                    <option value="team">Squadra</option>
                  </select>

                  <button
                    onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                    className="px-3 py-2 bg-gray-200 rounded-lg hover:bg-gray-300 transition-colors"
                  >
                    <i className={`fas fa-sort-${sortOrder === 'asc' ? 'up' : 'down'}`}></i>
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Risultati */}
          <div className="mb-4 text-sm text-gray-600">
            Mostrando {filteredPlayers.length} di {players.length} giocatori
          </div>

          {/* Lista Giocatori */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredPlayers.map(player => (
              <div
                key={player.id}
                onClick={() => setSelectedPlayer(player)}
                className="card-hover bg-white border border-gray-200 rounded-lg p-4 cursor-pointer transition-all duration-300"
              >
                <div className="flex justify-between items-start mb-2">
                  <h3 className="font-semibold text-lg">{player.name}</h3>
                  <span className={`text-xs px-2 py-1 rounded ${
                    player.priority === 'Alta' ? 'bg-red-100 text-red-700' :
                    player.priority === 'Media' ? 'bg-yellow-100 text-yellow-700' :
                    'bg-green-100 text-green-700'
                  }`}>
                    {player.priority || 'Bassa'}
                  </span>
                </div>

                <div className="text-sm text-gray-600 space-y-1">
                  <div className="flex justify-between">
                    <span>{player.team}</span>
                    <span>{player.general_role}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>{player.nationality}</span>
                    <span>{new Date().getFullYear() - (player.birth_year || 2000)} anni</span>
                  </div>
                  <div className="flex justify-between items-center mt-2">
                    <div className="text-yellow-500">
                      {'⭐'.repeat(player.potential_value || 0)}
                    </div>
                    <span className="text-xs text-gray-500">{player.scouting_date}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {filteredPlayers.length === 0 && (
            <div className="text-center py-12">
              <i className="fas fa-search text-4xl text-gray-300 mb-4"></i>
              <p className="text-gray-500">Nessun giocatore trovato con i filtri attuali</p>
              <button
                onClick={() => setFilters({
                  search: '', role: '', minPotential: '', maxAge: '',
                  nationality: '', foot: '', team: '', priority: ''
                })}
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
              >
                Resetta Filtri
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  // Rendering delle pagine
  const renderPage = () => {
    switch(currentPage) {
      case 'dashboard':
        return <Dashboard />;
      case 'database':
        return <Database />;
      case 'add':
        return (
          <PlayerForm
            player={selectedPlayer}
            onSave={addPlayer}
            onClose={() => {
              setCurrentPage('dashboard');
              setSelectedPlayer(null);
            }}
          />
        );
      case 'reports':
        return <div className="bg-white rounded-xl p-6 shadow-lg">
          <h2 className="text-2xl font-bold mb-6 gradient-text">Report e Statistiche</h2>
          <p className="text-gray-600">Sezione in sviluppo - Qui verranno mostrati report dettagliati e grafici avanzati.</p>
        </div>;
      default:
        return <Dashboard />;
    }
  };

  // Rendering principale
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 3000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            iconTheme: {
              primary: '#4ade80',
              secondary: '#fff',
            },
          },
          error: {
            iconTheme: {
              primary: '#ef4444',
              secondary: '#fff',
            },
          },
        }}
      />

      {/* Navigation */}
      <Navigation />

      {/* Notifiche */}
      {notification && (
        <div className={`fixed top-20 right-4 z-50 p-4 rounded-lg shadow-lg ${
          notification.type === 'success' ? 'bg-green-500' : 
          notification.type === 'error' ? 'bg-red-500' : 'bg-blue-500'
        } text-white`}>
          {notification.message}
        </div>
      )}

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-6">
        {renderPage()}
      </main>

      {/* Modal Dettaglio Giocatore */}
      {selectedPlayer && (
        <div className="modal-overlay" onClick={() => setSelectedPlayer(null)}>
          <div className="modal-content detail" onClick={e => e.stopPropagation()}>
            <button className="close-btn" onClick={() => setSelectedPlayer(null)}>×</button>
            <h2>{selectedPlayer.name}</h2>

            <div className="detail-grid">
              <div><strong>Anno:</strong> {selectedPlayer.birth_year}</div>
              <div><strong>Squadra:</strong> {selectedPlayer.team}</div>
              <div><strong>Ruolo:</strong> {selectedPlayer.general_role}</div>
              <div><strong>Piede:</strong> {selectedPlayer.preferred_foot}</div>
              {selectedPlayer.height && <div><strong>Altezza:</strong> {selectedPlayer.height}</div>}
              {selectedPlayer.market_value && <div><strong>Valore:</strong> {selectedPlayer.market_value}</div>}
              {selectedPlayer.priority && <div><strong>Priorità:</strong> {selectedPlayer.priority}</div>}
              <div><strong>Check:</strong> {selectedPlayer.check_type}</div>
            </div>

            {selectedPlayer.strong_points && (
              <div className="notes-section" style={{ background: '#d1fae5', borderLeft: '4px solid #10b981' }}>
                <h3>✅ Punti di Forza:</h3>
                <p>{selectedPlayer.strong_points}</p>
              </div>
            )}

            {selectedPlayer.weak_points && (
              <div className="notes-section" style={{ background: '#fee2e2', borderLeft: '4px solid #ef4444' }}>
                <h3>❌ Punti Deboli:</h3>
                <p>{selectedPlayer.weak_points}</p>
              </div>
            )}

            {selectedPlayer.notes && (
              <div className="notes-section">
                <h3>📝 Note Scout:</h3>
                <p>{selectedPlayer.notes}</p>
              </div>
            )}

            <div className="detail-actions">
              <button onClick={() => {
                setCurrentPage('add');
                setSelectedPlayer(null);
              }}>✏️ Modifica</button>
              <button onClick={() => deletePlayer(selectedPlayer.id)}>🗑️ Elimina</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;

