// App.js - SCOUTING SYSTEM CON INTERFACCIA COMPLETA IDENTICA AL FILE HTML
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { createClient } from '@supabase/supabase-js';
import toast, { Toaster } from 'react-hot-toast';
import './App.css';
import PlayerSearchTransfermarkt from './components/PlayerSearchTransfermarkt';

// âš ï¸ CREDENZIALI DA FILE .env
const SUPABASE_URL = process.env.REACT_APP_SUPABASE_URL;
const SUPABASE_KEY = process.env.REACT_APP_SUPABASE_ANON_KEY;

// Verifica che le credenziali siano presenti
if (!SUPABASE_URL || !SUPABASE_KEY) {
  console.error('âŒ Credenziali Supabase mancanti!', { SUPABASE_URL, SUPABASE_KEY });
}

// Inizializza client Supabase
const supabase = createClient(SUPABASE_URL, SUPABASE_KEY);

function App() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [notification, setNotification] = useState(null);
  const [viewMode, setViewMode] = useState('cards');

  // Funzione per caricare giocatori dal database
  const fetchPlayers = useCallback(async () => {
    try {
      setLoading(true);

      const { data, error } = await supabase
        .from('players')
        .select('*')
        .order('name', { ascending: true });

      if (error) throw error;

      setPlayers(data || []);
      showNotification(`Caricati ${data?.length || 0} giocatori`, 'success');

    } catch (error) {
      console.error('Errore caricamento:', error);
      showNotification('Errore nel caricamento dei giocatori', 'error');
    } finally {
      setLoading(false);
    }
  }, []);

  // Sistema di notifiche
  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  // Carica giocatori all'avvio
  useEffect(() => {
    fetchPlayers();

    // Setup subscription per aggiornamenti real-time
    const subscription = supabase
      .channel('players_channel')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'players' },
        (payload) => {
          console.log('Cambiamento rilevato:', payload);
          fetchPlayers(); // Ricarica quando ci sono modifiche
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [fetchPlayers]);

  // Aggiungi nuovo giocatore
  const addPlayer = async (playerData) => {
    try {
      const { data, error } = await supabase
        .from('players')
        .insert([{
          ...playerData,
          created_at: new Date().toISOString(),
          scouting_date: new Date().toISOString().split('T')[0]
        }])
        .select()
        .single();

      if (error) throw error;

      setPlayers([...players, data]);
      showNotification('âœ… Giocatore aggiunto con successo!', 'success');
      setCurrentPage('database');

    } catch (error) {
      console.error('Errore inserimento:', error);
      showNotification(`âŒ Errore nell'aggiunta: ${error.message}`, 'error');
    }
  };

  // Aggiorna giocatore esistente
  const updatePlayer = async (id, updates) => {
    try {
      const { data, error } = await supabase
        .from('players')
        .update({
          ...updates,
          updated_at: new Date().toISOString()
        })
        .eq('id', id)
        .select()
        .single();

      if (error) throw error;

      setPlayers(players.map(p => p.id === id ? data : p));
      showNotification('âœ… Giocatore aggiornato!', 'success');

    } catch (error) {
      console.error('Errore aggiornamento:', error);
      showNotification(`âŒ Errore nell'aggiornamento: ${error.message}`, 'error');
    }
  };

  // Elimina giocatore
  const deletePlayer = async (id) => {
    if (!window.confirm('Sei sicuro di voler eliminare questo giocatore?')) return;

    try {
      const { error } = await supabase
        .from('players')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setPlayers(players.filter(p => p.id !== id));
      showNotification('ðŸ—‘ï¸ Giocatore eliminato', 'info');
      setSelectedPlayer(null);

    } catch (error) {
      console.error('Errore eliminazione:', error);
      showNotification('âŒ Errore nell\'eliminazione', 'error');
    }
  };

  // ========================================
  // COMPONENTE NAVIGATION - HEADER BLU
  // ========================================
  function Navigation() {
    const navItems = [
      { id: 'dashboard', label: 'Dashboard', icon: 'fa-home' },
      { id: 'database', label: 'Database', icon: 'fa-database' },
      { id: 'add', label: 'Aggiungi Giocatore', icon: 'fa-plus-circle' },
      { id: 'reports', label: 'Report', icon: 'fa-chart-bar' }
    ];

    return (
      <nav className="sticky top-0 z-50" style={{ background: 'linear-gradient(135deg, #4F46E5 0%, #3B82F6 100%)' }}>
        <div className="max-w-7xl mx-auto px-6">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-white rounded-full flex items-center justify-center">
                <i className="fas fa-futbol text-2xl" style={{ color: '#4F46E5' }}></i>
              </div>
              <div>
                <h1 className="text-xl font-bold text-white">Scouting System</h1>
                <p className="text-xs text-blue-100">Database Professionale</p>
              </div>
            </div>

            {/* Navigation Buttons */}
            <div className="flex space-x-2">
              {navItems.map(item => (
                <button
                  key={item.id}
                  onClick={() => setCurrentPage(item.id)}
                  className={`px-4 py-2 rounded-lg transition-all duration-300 flex items-center space-x-2 ${
                    currentPage === item.id 
                      ? 'bg-white text-blue-600 shadow-lg' 
                      : 'text-white hover:bg-white hover:bg-opacity-20'
                  }`}
                >
                  <i className={`fas ${item.icon}`}></i>
                  <span className="hidden md:inline font-medium">{item.label}</span>
                </button>
              ))}
            </div>

            {/* User Info */}
            <div className="flex items-center space-x-3">
              <span className="text-sm text-white font-medium">
                {players.length} giocatori
              </span>
              <button className="w-9 h-9 rounded-full bg-white bg-opacity-20 hover:bg-opacity-30 flex items-center justify-center transition-all">
                <i className="fas fa-user text-white"></i>
              </button>
            </div>
          </div>
        </div>
      </nav>
    );
  }

  // ========================================
  // COMPONENTE FORM GIOCATORE CON INTEGRAZIONE TRANSFERMARKT
  // ========================================
  function PlayerForm({ player, onSave, onClose }) {
    const [formData, setFormData] = useState(player || {
      name: '',
      birth_year: 2000,
      team: '',
      nationality: '',
      height: '',
      weight: '',
      general_role: 'Centrocampo',
      specific_position: '',
      functions_labels: '',
      preferred_foot: 'Destro',
      athleticism: '',
      athletic_evaluation: null,
      key_characteristics: '',
      potential_value: 3,
      current_value: 2,
      data_potential_value: 3,
      market_value: '',
      contract_expiry: '',
      transfermarkt_link: '',
      notes: '',
      priority: 'Media',
      recommended_action: 'Monitorare',
      director_feedback: 'Da valutare',
      check_type: 'Live/Video',
      outcome: '',
      scout_name: '',
      scouting_date: new Date().toISOString().split('T')[0],
      injuries: '',
      strong_points: '',
      weak_points: '',
      comparison: ''
    });

    // âœ… FUNZIONE PER GESTIRE L'IMPORTAZIONE DA TRANSFERMARKT
    const handleImportFromTransfermarkt = (importedData) => {
      toast.success('âœ… Dati importati da Transfermarkt!', {
        duration: 3000,
        icon: 'ðŸŒ'
      });

      // Aggiorna il form con i dati importati (mantieni i dati esistenti non sovrascritti)
      setFormData(prevData => ({
        ...prevData,
        ...importedData,
        // Mantieni alcuni campi esistenti se giÃ  compilati
        notes: importedData.notes + (prevData.notes ? '\n\n' + prevData.notes : '')
      }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      if (player?.id) {
        updatePlayer(player.id, formData);
      } else {
        onSave(formData);
      }
      onClose();
    };

    return (
      <div className="modal-overlay">
        <div className="modal-content" style={{ maxWidth: '900px', maxHeight: '90vh', overflowY: 'auto' }}>
          <button className="close-btn" onClick={onClose}>Ã—</button>

          <h2 className="text-2xl font-bold mb-4 text-blue-600">
            {player ? 'âœï¸ Modifica Giocatore' : 'âž• Nuovo Giocatore'}
          </h2>

          {/* ========================================
              SEZIONE IMPORTAZIONE DA TRANSFERMARKT
              ======================================== */}
          {!player && (
            <div className="mb-6 p-6 bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl border-2 border-blue-200">
              <div className="flex items-center gap-3 mb-4">
                <div className="w-10 h-10 bg-blue-500 rounded-full flex items-center justify-center">
                  <i className="fas fa-globe text-white"></i>
                </div>
                <div>
                  <h3 className="font-bold text-gray-800">Importa da Transfermarkt</h3>
                  <p className="text-sm text-gray-600">Cerca e importa automaticamente i dati di un giocatore</p>
                </div>
              </div>

              <PlayerSearchTransfermarkt onSelectPlayer={handleImportFromTransfermarkt} />

              <div className="mt-3 p-3 bg-blue-100 rounded-lg">
                <p className="text-xs text-blue-800">
                  <i className="fas fa-info-circle mr-1"></i>
                  <strong>Suggerimento:</strong> Cerca il giocatore su Transfermarkt per compilare automaticamente 
                  nome, data di nascita, squadra, nazionalitÃ , ruolo, altezza, piede e valore di mercato.
                </p>
              </div>
            </div>
          )}

          {/* ========================================
              FORM PRINCIPALE
              ======================================== */}
          <form onSubmit={handleSubmit}>
            <div className="form-grid">
              {/* Informazioni Base */}
              <div className="form-group">
                <label>Nome Completo *</label>
                <input
                  type="text"
                  required
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  placeholder="Mario Rossi"
                />
              </div>

              <div className="form-group">
                <label>Anno di Nascita</label>
                <input
                  type="number"
                  min="1990"
                  max="2010"
                  value={formData.birth_year}
                  onChange={(e) => setFormData({...formData, birth_year: parseInt(e.target.value)})}
                />
                <small className="text-gray-500">
                  {formData.birth_year && `EtÃ : ${new Date().getFullYear() - formData.birth_year} anni`}
                </small>
              </div>

              <div className="form-group">
                <label>Squadra</label>
                <input
                  type="text"
                  value={formData.team}
                  onChange={(e) => setFormData({...formData, team: e.target.value})}
                  placeholder="AC Milan, Juventus..."
                />
              </div>

              <div className="form-group">
                <label>NazionalitÃ </label>
                <input
                  type="text"
                  value={formData.nationality}
                  onChange={(e) => setFormData({...formData, nationality: e.target.value})}
                  placeholder="Italia, Francia..."
                />
              </div>

              {/* Caratteristiche Fisiche */}
              <div className="form-group">
                <label>Altezza (cm)</label>
                <input
                  type="text"
                  value={formData.height}
                  onChange={(e) => setFormData({...formData, height: e.target.value})}
                  placeholder="180"
                />
              </div>

              <div className="form-group">
                <label>Peso (kg)</label>
                <input
                  type="text"
                  value={formData.weight}
                  onChange={(e) => setFormData({...formData, weight: e.target.value})}
                  placeholder="75"
                />
              </div>

              {/* Ruolo e Posizione */}
              <div className="form-group">
                <label>Ruolo Generale</label>
                <select
                  value={formData.general_role}
                  onChange={(e) => setFormData({...formData, general_role: e.target.value})}
                >
                  <option value="Portiere">ðŸ¥… Portiere</option>
                  <option value="Difensore">ðŸ›¡ï¸ Difensore</option>
                  <option value="Terzino">â†”ï¸ Terzino</option>
                  <option value="Centrocampo">âš½ Centrocampo</option>
                  <option value="Ala">ðŸ¦… Ala</option>
                  <option value="Attaccante">ðŸŽ¯ Attaccante</option>
                </select>
              </div>

              <div className="form-group">
                <label>Posizione Specifica</label>
                <input
                  type="text"
                  value={formData.specific_position}
                  onChange={(e) => setFormData({...formData, specific_position: e.target.value})}
                  placeholder="Mediano, Ala destra..."
                />
              </div>

              <div className="form-group">
                <label>Piede Preferito</label>
                <select
                  value={formData.preferred_foot}
                  onChange={(e) => setFormData({...formData, preferred_foot: e.target.value})}
                >
                  <option value="Destro">ðŸ‘‰ Destro</option>
                  <option value="Sinistro">ðŸ‘ˆ Sinistro</option>
                  <option value="Ambidestro">ðŸ‘ Ambidestro</option>
                </select>
              </div>

              {/* Valutazioni */}
              <div className="form-group">
                <label>Valore Attuale (1-5)</label>
                <input
                  type="range"
                  min="1"
                  max="5"
                  value={formData.current_value}
                  onChange={(e) => setFormData({...formData, current_value: parseInt(e.target.value)})}
                />
                <span className="text-yellow-500 text-lg font-bold">
                  {formData.current_value} â­
                </span>
              </div>

              <div className="form-group">
                <label>Potenziale (1-5)</label>
                <input
                  type="range"
                  min="1"
                  max="5"
                  value={formData.potential_value}
                  onChange={(e) => setFormData({...formData, potential_value: parseInt(e.target.value)})}
                />
                <span className="text-green-500 text-lg font-bold">
                  {formData.potential_value} â­
                </span>
              </div>

              <div className="form-group">
                <label>Potenziale Dati (1-5)</label>
                <input
                  type="range"
                  min="1"
                  max="5"
                  value={formData.data_potential_value}
                  onChange={(e) => setFormData({...formData, data_potential_value: parseInt(e.target.value)})}
                />
                <span className="text-purple-500 text-lg font-bold">
                  {formData.data_potential_value} â­
                </span>
              </div>

              {/* Info Economiche */}
              <div className="form-group">
                <label>Valore di Mercato</label>
                <input
                  type="text"
                  value={formData.market_value}
                  onChange={(e) => setFormData({...formData, market_value: e.target.value})}
                  placeholder="â‚¬2.5M, â‚¬500K..."
                />
              </div>

              <div className="form-group">
                <label>Contratto fino al</label>
                <input
                  type="text"
                  value={formData.contract_expiry}
                  onChange={(e) => setFormData({...formData, contract_expiry: e.target.value})}
                  placeholder="2026, Giugno 2025..."
                />
              </div>

              {/* PrioritÃ  e Azioni */}
              <div className="form-group">
                <label>PrioritÃ </label>
                <select
                  value={formData.priority}
                  onChange={(e) => setFormData({...formData, priority: e.target.value})}
                >
                  <option value="Alta">ðŸ”´ Alta</option>
                  <option value="Media">ðŸŸ¡ Media</option>
                  <option value="Bassa">ðŸŸ¢ Bassa</option>
                </select>
              </div>

              <div className="form-group">
                <label>Azione Raccomandata</label>
                <select
                  value={formData.recommended_action}
                  onChange={(e) => setFormData({...formData, recommended_action: e.target.value})}
                >
                  <option value="Monitorare">ðŸ‘€ Monitorare</option>
                  <option value="Approfondire">ðŸ” Approfondire</option>
                  <option value="Contattare">ðŸ“ž Contattare</option>
                  <option value="Scartare">âŒ Scartare</option>
                </select>
              </div>

              <div className="form-group">
                <label>Feedback Direttore</label>
                <select
                  value={formData.director_feedback}
                  onChange={(e) => setFormData({...formData, director_feedback: e.target.value})}
                >
                  <option value="Mi piace">ðŸ‘ Mi piace</option>
                  <option value="Da valutare">ðŸ¤” Da valutare</option>
                  <option value="Non mi convince">ðŸ‘Ž Non mi convince</option>
                  <option value="Interessante">â­ Interessante</option>
                </select>
              </div>

              <div className="form-group">
                <label>Tipo di Check</label>
                <select
                  value={formData.check_type}
                  onChange={(e) => setFormData({...formData, check_type: e.target.value})}
                >
                  <option value="Live">ðŸ”´ Live</option>
                  <option value="Video">ðŸ“¹ Video</option>
                  <option value="Live/Video">ðŸ”´ðŸ“¹ Live/Video</option>
                  <option value="Dati">ðŸ“Š Dati</option>
                </select>
              </div>

              {/* Link Transfermarkt */}
              {formData.transfermarkt_link && (
                <div className="form-group full-width">
                  <label>Link Transfermarkt</label>
                  <input
                    type="text"
                    value={formData.transfermarkt_link}
                    onChange={(e) => setFormData({...formData, transfermarkt_link: e.target.value})}
                    className="bg-gray-50"
                    readOnly
                  />
                  <a 
                    href={formData.transfermarkt_link} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-xs text-blue-600 hover:underline mt-1 inline-block"
                  >
                    ðŸ”— Apri su Transfermarkt
                  </a>
                </div>
              )}

              {/* Caratteristiche */}
              <div className="form-group full-width">
                <label>Punti di Forza</label>
                <textarea
                  rows="2"
                  value={formData.strong_points}
                  onChange={(e) => setFormData({...formData, strong_points: e.target.value})}
                  placeholder="Visione di gioco, tiro da fuori, inserimenti..."
                />
              </div>

              <div className="form-group full-width">
                <label>Punti Deboli</label>
                <textarea
                  rows="2"
                  value={formData.weak_points}
                  onChange={(e) => setFormData({...formData, weak_points: e.target.value})}
                  placeholder="VelocitÃ , contrasti aerei, marcatura..."
                />
              </div>

              {/* Note Scout */}
              <div className="form-group full-width">
                <label>Note Scout</label>
                <textarea
                  rows="4"
                  value={formData.notes}
                  onChange={(e) => setFormData({...formData, notes: e.target.value})}
                  placeholder="Report dettagliato: stile di gioco, caratteristiche principali, potenziale di crescita..."
                />
              </div>
            </div>

            {/* Azioni Form */}
            <div className="form-actions" style={{ marginTop: '2rem', paddingTop: '1rem', borderTop: '2px solid #e5e7eb' }}>
              <button type="button" onClick={onClose} className="btn-secondary">
                âŒ Annulla
              </button>
              <button type="submit" className="btn-primary">
                {player ? 'ðŸ’¾ Salva Modifiche' : 'âž• Aggiungi Giocatore'}
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }

  // Componente Card Giocatore
  function PlayerCard({ player }) {
    return (
      <div className="player-card" onClick={() => setSelectedPlayer(player)}>
        <div className="player-header">
          <h3>{player.name}</h3>
          <span className="player-year">{player.birth_year}</span>
        </div>
        <div className="player-info">
          <p>ðŸ† {player.team || 'N/A'}</p>
          <p>âš½ {player.general_role}</p>
          <p>ðŸ‘Ÿ {player.preferred_foot}</p>
          {player.market_value && <p>ðŸ’° {player.market_value}</p>}
        </div>
        <div className="player-ratings">
          <div>Attuale: {'â­'.repeat(player.current_value || 0)}</div>
          <div>Potenziale: {'â­'.repeat(player.potential_value || 0)}</div>
        </div>
        <div className="player-actions">
          <button onClick={(e) => {
            e.stopPropagation();
            setSelectedPlayer(player);
            setCurrentPage('add');
          }}>âœï¸</button>
          <button onClick={(e) => {
            e.stopPropagation();
            deletePlayer(player.id);
          }}>ðŸ—‘ï¸</button>
        </div>
      </div>
    );
  }

  // Componente Dashboard
  function Dashboard() {
    const stats = {
      totalPlayers: players.length,
      avgPotential: players.length > 0 ? (players.reduce((acc, p) => acc + (p.potential_value || 3), 0) / players.length).toFixed(1) : '0',
      highPotential: players.filter(p => (p.potential_value || 0) >= 4).length,
      positions: {
        'Portiere': players.filter(p => p.general_role === 'Portiere').length,
        'Difensore': players.filter(p => p.general_role === 'Difensore').length,
        'Centrocampo': players.filter(p => p.general_role === 'Centrocampo').length,
        'Attaccante': players.filter(p => p.general_role === 'Attaccante').length
      }
    };

    const recentPlayers = [...players].sort((a, b) => 
      new Date(b.scouting_date || b.created_at) - new Date(a.scouting_date || a.created_at)
    ).slice(0, 3);

    return (
      <div className="fade-in space-y-6">
        <div className="bg-white rounded-xl p-6 shadow-lg">
          <h2 className="text-2xl font-bold mb-6 gradient-text">Dashboard Overview</h2>

          {/* Stats Grid */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
            <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl p-4 text-white">
              <i className="fas fa-users text-3xl mb-2 opacity-80"></i>
              <div className="text-3xl font-bold">{stats.totalPlayers}</div>
              <div className="text-sm opacity-90">Totale Giocatori</div>
            </div>

            <div className="bg-gradient-to-br from-green-500 to-green-600 rounded-xl p-4 text-white">
              <i className="fas fa-star text-3xl mb-2 opacity-80"></i>
              <div className="text-3xl font-bold">{stats.avgPotential}</div>
              <div className="text-sm opacity-90">Media Potenziale</div>
            </div>

            <div className="bg-gradient-to-br from-purple-500 to-purple-600 rounded-xl p-4 text-white">
              <i className="fas fa-gem text-3xl mb-2 opacity-80"></i>
              <div className="text-3xl font-bold">{stats.highPotential}</div>
              <div className="text-sm opacity-90">Alto Potenziale (4+)</div>
            </div>

            <div className="bg-gradient-to-br from-orange-500 to-orange-600 rounded-xl p-4 text-white">
              <i className="fas fa-chart-line text-3xl mb-2 opacity-80"></i>
