// App.js - VERSIONE COMPLETA CON DATABASE SUPABASE + INTEGRAZIONE TRANSFERMARKT
// 
// ISTRUZIONI SETUP:
// 1. Crea progetto: npx create-react-app scouting-app
// 2. Installa: npm install @supabase/supabase-js react-hot-toast
// 3. Crea account su supabase.com e ottieni credenziali
// 4. Sostituisci SUPABASE_URL e SUPABASE_KEY nel file .env
// 5. Crea il file src/components/PlayerSearchTransfermarkt.js
// 6. Crea tabella 'players' in Supabase con schema fornito

import React, { useState, useEffect, useCallback } from 'react';
import { createClient } from '@supabase/supabase-js';
import toast, { Toaster } from 'react-hot-toast';
import './App.css';
import PlayerSearchTransfermarkt from './components/PlayerSearchTransfermarkt';

// âš ï¸ CREDENZIALI DA FILE .env
const SUPABASE_URL = process.env.REACT_APP_SUPABASE_URL;
const SUPABASE_KEY = process.env.REACT_APP_SUPABASE_ANON_KEY;

// Verifica che le credenziali siano presenti
if (!SUPABASE_URL || !SUPABASE_KEY) {
  console.error('âŒ Credenziali Supabase mancanti!', { SUPABASE_URL, SUPABASE_KEY });
}

// Inizializza client Supabase
const supabase = createClient(SUPABASE_URL, SUPABASE_KEY);

function App() {
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('');

  // Funzione per caricare giocatori dal database
  const fetchPlayers = useCallback(async () => {
    try {
      setLoading(true);

      let query = supabase
        .from('players')
        .select('*')
        .order('name', { ascending: true });

      // Applica filtri se presenti
      if (searchTerm) {
        query = query.or(`name.ilike.%${searchTerm}%,team.ilike.%${searchTerm}%`);
      }

      if (filterRole) {
        query = query.eq('general_role', filterRole);
      }

      const { data, error } = await query;

      if (error) throw error;

      setPlayers(data || []);
      toast.success(`Caricati ${data?.length || 0} giocatori`);

    } catch (error) {
      console.error('Errore caricamento:', error);
      toast.error('Errore nel caricamento dei giocatori');
    } finally {
      setLoading(false);
    }
  }, [searchTerm, filterRole]);

  // Carica giocatori all'avvio
  useEffect(() => {
    fetchPlayers();

    // Setup subscription per aggiornamenti real-time
    const subscription = supabase
      .channel('players_channel')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'players' },
        (payload) => {
          console.log('Cambiamento rilevato:', payload);
          fetchPlayers(); // Ricarica quando ci sono modifiche
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [fetchPlayers]);

  // Aggiungi nuovo giocatore
  async function addPlayer(playerData) {
    try {
      const { data, error } = await supabase
        .from('players')
        .insert([{
          ...playerData,
          created_at: new Date().toISOString(),
          created_by: null,
          updated_by: null
        }])
        .select()
        .single();

      if (error) throw error;

      setPlayers([...players, data]);
      toast.success('âœ… Giocatore aggiunto con successo!');
      setShowAddForm(false);

    } catch (error) {
      console.error('Errore inserimento:', error);
      toast.error(`âŒ Errore nell'aggiunta del giocatore: ${error.message}`);
    }
  }

  // Aggiorna giocatore esistente
  async function updatePlayer(id, updates) {
    try {
      const { data, error } = await supabase
        .from('players')
        .update({
          ...updates,
          updated_at: new Date().toISOString(),
          updated_by: null
        })
        .eq('id', id)
        .select()
        .single();

      if (error) throw error;

      // Aggiorna stato locale
      setPlayers(players.map(p => p.id === id ? data : p));
      toast.success('âœ… Giocatore aggiornato!');

    } catch (error) {
      console.error('Errore aggiornamento:', error);
      toast.error(`âŒ Errore nell'aggiornamento: ${error.message}`);
    }
  }

  // Elimina giocatore
  async function deletePlayer(id) {
    if (!window.confirm('Sei sicuro di voler eliminare questo giocatore?')) return;

    try {
      const { error } = await supabase
        .from('players')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setPlayers(players.filter(p => p.id !== id));
      toast.success('ðŸ—‘ï¸ Giocatore eliminato');
      setSelectedPlayer(null);

    } catch (error) {
      console.error('Errore eliminazione:', error);
      toast.error('âŒ Errore nell\'eliminazione');
    }
  }

  // ========================================
  // COMPONENTE FORM GIOCATORE CON INTEGRAZIONE TRANSFERMARKT
  // ========================================
  function PlayerForm({ player, onSave, onClose }) {
    const [formData, setFormData] = useState(player || {
      name: '',
      birth_year: 2000,
      team: '',
      nationality: '',
      height: '',
      weight: '',
      general_role: 'Centrocampo',
      specific_position: '',
      functions_labels: '',
      preferred_foot: 'Destro',
      athleticism: '',
      athletic_evaluation: null,
      key_characteristics: '',
      potential_value: 3,
      current_value: 2,
      data_potential_value: 3,
      market_value: '',
      contract_expiry: '',
      transfermarkt_link: '',
      notes: '',
      priority: 'Media',
      recommended_action: 'Monitorare',
      director_feedback: 'Da valutare',
      check_type: 'Live/Video',
      outcome: '',
      scout_name: '',
      scouting_date: new Date().toISOString().split('T')[0],
      injuries: '',
      strong_points: '',
      weak_points: '',
      comparison: ''
    });

    // âœ… FUNZIONE PER GESTIRE L'IMPORTAZIONE DA TRANSFERMARKT
    const handleImportFromTransfermarkt = (importedData) => {
      toast.success('âœ… Dati importati da Transfermarkt!', {
        duration: 3000,
        icon: 'ðŸŒ'
      });

      // Aggiorna il form con i dati importati (mantieni i dati esistenti non sovrascritti)
      setFormData(prevData => ({
        ...prevData,
        ...importedData,
        // Mantieni alcuni campi esistenti se giÃ  compilati
        notes: importedData.notes + (prevData.notes ? '\n\n' + prevData.notes : '')
      }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      if (player?.id) {
        updatePlayer(player.id, formData);
      } else {
        onSave(formData);
      }
      onClose();
    };

    return (
      <div className="modal-overlay">
        <div className="modal-content" style={{ maxWidth: '900px', maxHeight: '90vh', overflowY: 'auto' }}>
          <button className="close-btn" onClick={onClose}>Ã—</button>

          <h2 className="text-2xl font-bold mb-4 text-blue-600">
            {player ? 'âœï¸ Modifica Giocatore' : 'âž• Nuovo Giocatore'}
          </h2>

          {/* ========================================
              SEZIONE IMPORTAZIONE DA TRANSFERMARKT
              ======================================== */}
          {!player && (
            <div className="transfermarkt-section">
              <div className="transfermarkt-header">
                <div className="transfermarkt-icon">
                  <i className="fas fa-globe"></i>
                </div>
                <div className="transfermarkt-title">
                  <h3>Importa da Transfermarkt</h3>
                  <p>Cerca e importa automaticamente i dati di un giocatore</p>
                </div>
              </div>

              <PlayerSearchTransfermarkt onSelectPlayer={handleImportFromTransfermarkt} />

              <div style={{marginTop: '12px', padding: '12px', backgroundColor: '#fef9c3', borderRadius: '8px', border: '1px solid #fde047'}}>
                <p style={{fontSize: '0.85rem', color: '#854d0e', margin: '0 0 8px 0'}}>
                  <i className="fas fa-info-circle" style={{marginRight: '6px'}}></i>
                  Database demo con giocatori famosi - I dati verranno compilati automaticamente nel form
                </p>
                <div className="available-players">
                  <span className="player-badge">Erling Haaland</span>
                  <span className="player-badge">Kylian MbappÃ©</span>
                  <span className="player-badge">Lionel Messi</span>
                  <span className="player-badge">Jude Bellingham</span>
                  <span className="player-badge">Pedri GonzÃ¡lez</span>
                  <span className="player-badge">Vinicius Junior</span>
                </div>
              </div>

              <div className="transfermarkt-info">
                <p>
                  <i className="fas fa-lightbulb" style={{marginRight: '6px'}}></i>
                  <strong>Suggerimento:</strong> Cerca il giocatore su Transfermarkt per compilare automaticamente 
                  nome, data di nascita, squadra, nazionalitÃ , ruolo, altezza, piede e valore di mercato.
                </p>
              </div>
            </div>
          )}

          {/* ========================================
              FORM PRINCIPALE
              ======================================== */}
          <form onSubmit={handleSubmit}>
            <div className="form-grid">
              {/* Sezione Informazioni Base */}
              <div className="form-section-title">
                <i className="fas fa-user"></i>
                Informazioni Base
              </div>

              <div className="form-group">
                <label>Nome Completo</label>
                <input
                  type="text"
                  required
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  placeholder="Mario Rossi"
                />
              </div>

              <div className="form-group">
                <label>Anno di Nascita</label>
                <input
                  type="number"
                  min="1990"
                  max="2010"
                  value={formData.birth_year}
                  onChange={(e) => setFormData({...formData, birth_year: parseInt(e.target.value)})}
                />
                <small className="text-gray-500">
                  {formData.birth_year && `EtÃ : ${new Date().getFullYear() - formData.birth_year} anni`}
                </small>
              </div>

              <div className="form-group">
                <label>Squadra</label>
                <input
                  type="text"
                  value={formData.team}
                  onChange={(e) => setFormData({...formData, team: e.target.value})}
                  placeholder="AC Milan, Juventus..."
                />
              </div>

              <div className="form-group">
                <label>NazionalitÃ </label>
                <input
                  type="text"
                  value={formData.nationality}
                  onChange={(e) => setFormData({...formData, nationality: e.target.value})}
                  placeholder="Italia, Francia..."
                />
              </div>

              {/* Sezione Caratteristiche Fisiche */}
              <div className="form-section-title">
                <i className="fas fa-ruler-vertical"></i>
                Caratteristiche Fisiche
              </div>

              <div className="form-group">
                <label>Altezza (cm)</label>
                <input
                  type="text"
                  value={formData.height}
                  onChange={(e) => setFormData({...formData, height: e.target.value})}
                  placeholder="180"
                />
              </div>

              <div className="form-group">
                <label>Peso (kg)</label>
                <input
                  type="text"
                  value={formData.weight}
                  onChange={(e) => setFormData({...formData, weight: e.target.value})}
                  placeholder="75"
                />
              </div>

              {/* Sezione Ruolo e Posizione */}
              <div className="form-section-title">
                <i className="fas fa-futbol"></i>
                Ruolo e Posizione
              </div>

              <div className="form-group">
                <label>Ruolo Generale</label>
                <select
                  value={formData.general_role}
                  onChange={(e) => setFormData({...formData, general_role: e.target.value})}
                >
                  <option value="Portiere">ðŸ¥… Portiere</option>
                  <option value="Difensore">ðŸ›¡ï¸ Difensore</option>
                  <option value="Terzino">â†”ï¸ Terzino</option>
                  <option value="Centrocampo">âš½ Centrocampo</option>
                  <option value="Ala">ðŸ¦… Ala</option>
                  <option value="Attaccante">ðŸŽ¯ Attaccante</option>
                </select>
              </div>

              <div className="form-group">
                <label>Posizione Specifica</label>
                <input
                  type="text"
                  value={formData.specific_position}
                  onChange={(e) => setFormData({...formData, specific_position: e.target.value})}
                  placeholder="Mediano, Ala destra..."
                />
              </div>

              <div className="form-group">
                <label>Piede Preferito</label>
                <select
                  value={formData.preferred_foot}
                  onChange={(e) => setFormData({...formData, preferred_foot: e.target.value})}
                >
                  <option value="Destro">ðŸ‘‰ Destro</option>
                  <option value="Sinistro">ðŸ‘ˆ Sinistro</option>
                  <option value="Ambidestro">ðŸ‘ Ambidestro</option>
                </select>
              </div>

              {/* Sezione Valutazioni */}
              <div className="form-section-title">
                <i className="fas fa-star"></i>
                Valutazioni
              </div>

              <div className="form-group">
                <label>Valore Attuale (1-5)</label>
                <div className="rating-container">
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={formData.current_value}
                    onChange={(e) => setFormData({...formData, current_value: parseInt(e.target.value)})}
                    style={{flex: 1}}
                  />
                  <span className="rating-label" style={{color: '#f59e0b'}}>
                    {formData.current_value} â­
                  </span>
                </div>
              </div>

              <div className="form-group">
                <label>Potenziale (1-5)</label>
                <div className="rating-container">
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={formData.potential_value}
                    onChange={(e) => setFormData({...formData, potential_value: parseInt(e.target.value)})}
                    style={{flex: 1}}
                  />
                  <span className="rating-label" style={{color: '#10b981'}}>
                    {formData.potential_value} â­
                  </span>
                </div>
              </div>

              <div className="form-group">
                <label>Potenziale Dati (1-5)</label>
                <div className="rating-container">
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={formData.data_potential_value}
                    onChange={(e) => setFormData({...formData, data_potential_value: parseInt(e.target.value)})}
                    style={{flex: 1}}
                  />
                  <span className="rating-label" style={{color: '#8b5cf6'}}>
                    {formData.data_potential_value} â­
                  </span>
                </div>
              </div>

              {/* Sezione Info Economiche */}
              <div className="form-section-title">
                <i className="fas fa-euro-sign"></i>
                Informazioni Economiche
              </div>

              <div className="form-group">
                <label>Valore di Mercato</label>
                <input
                  type="text"
                  value={formData.market_value}
                  onChange={(e) => setFormData({...formData, market_value: e.target.value})}
                  placeholder="â‚¬2.5M, â‚¬500K..."
                />
              </div>

              <div className="form-group">
                <label>Contratto fino al</label>
                <input
                  type="text"
                  value={formData.contract_expiry}
                  onChange={(e) => setFormData({...formData, contract_expiry: e.target.value})}
                  placeholder="2026, Giugno 2025..."
                />
              </div>

              {/* Sezione PrioritÃ  e Azioni */}
              <div className="form-section-title">
                <i className="fas fa-tasks"></i>
                PrioritÃ  e Azioni Raccomandate
              </div>

              <div className="form-group">
                <label>PrioritÃ </label>
                <select
                  value={formData.priority}
                  onChange={(e) => setFormData({...formData, priority: e.target.value})}
                >
                  <option value="Alta">ðŸ”´ Alta</option>
                  <option value="Media">ðŸŸ¡ Media</option>
                  <option value="Bassa">ðŸŸ¢ Bassa</option>
                </select>
              </div>

              <div className="form-group">
                <label>Azione Raccomandata</label>
                <select
                  value={formData.recommended_action}
                  onChange={(e) => setFormData({...formData, recommended_action: e.target.value})}
                >
                  <option value="Monitorare">ðŸ‘€ Monitorare</option>
                  <option value="Approfondire">ðŸ” Approfondire</option>
                  <option value="Contattare">ðŸ“ž Contattare</option>
                  <option value="Scartare">âŒ Scartare</option>
                </select>
              </div>

              <div className="form-group">
                <label>Feedback Direttore</label>
                <select
                  value={formData.director_feedback}
                  onChange={(e) => setFormData({...formData, director_feedback: e.target.value})}
                >
                  <option value="Mi piace">ðŸ‘ Mi piace</option>
                  <option value="Da valutare">ðŸ¤” Da valutare</option>
                  <option value="Non mi convince">ðŸ‘Ž Non mi convince</option>
                  <option value="Interessante">â­ Interessante</option>
                </select>
              </div>

              <div className="form-group">
                <label>Tipo di Check</label>
                <select
                  value={formData.check_type}
                  onChange={(e) => setFormData({...formData, check_type: e.target.value})}
                >
                  <option value="Live">ðŸ”´ Live</option>
                  <option value="Video">ðŸ“¹ Video</option>
                  <option value="Live/Video">ðŸ”´ðŸ“¹ Live/Video</option>
                  <option value="Dati">ðŸ“Š Dati</option>
                </select>
              </div>

              {/* Link Transfermarkt */}
              {formData.transfermarkt_link && (
                <div className="form-group full-width">
                  <label>Link Transfermarkt</label>
                  <input
                    type="text"
                    value={formData.transfermarkt_link}
                    onChange={(e) => setFormData({...formData, transfermarkt_link: e.target.value})}
                    className="bg-gray-50"
                    readOnly
                  />
                  <a 
                    href={formData.transfermarkt_link} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-xs text-blue-600 hover:underline mt-1 inline-block"
                  >
                    ðŸ”— Apri su Transfermarkt
                  </a>
                </div>
              )}

              {/* Sezione Caratteristiche */}
              <div className="form-section-title">
                <i className="fas fa-clipboard-list"></i>
                Caratteristiche e Note
              </div>

              <div className="form-group full-width">
                <label>Punti di Forza</label>
                <textarea
                  rows="2"
                  value={formData.strong_points}
                  onChange={(e) => setFormData({...formData, strong_points: e.target.value})}
                  placeholder="Visione di gioco, tiro da fuori, inserimenti..."
                />
              </div>

              <div className="form-group full-width">
                <label>Punti Deboli</label>
                <textarea
                  rows="2"
                  value={formData.weak_points}
                  onChange={(e) => setFormData({...formData, weak_points: e.target.value})}
                  placeholder="VelocitÃ , contrasti aerei, marcatura..."
                />
              </div>

              {/* Note Scout */}
              <div className="form-group full-width">
                <label>Note Scout</label>
                <textarea
                  rows="4"
                  value={formData.notes}
                  onChange={(e) => setFormData({...formData, notes: e.target.value})}
                  placeholder="Report dettagliato: stile di gioco, caratteristiche principali, potenziale di crescita..."
                />
              </div>
            </div>

            {/* Azioni Form */}
            <div className="form-actions" style={{ marginTop: '2rem', paddingTop: '1rem', borderTop: '2px solid #e5e7eb' }}>
              <button type="button" onClick={onClose} className="btn-secondary">
                âŒ Annulla
              </button>
              <button type="submit" className="btn-primary">
                {player ? 'ðŸ’¾ Salva Modifiche' : 'âž• Aggiungi Giocatore'}
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }

  // Componente Card Giocatore
  function PlayerCard({ player }) {
    return (
      <div className="player-card" onClick={() => setSelectedPlayer(player)}>
        <div className="player-header">
          <h3>{player.name}</h3>
          <span className="player-year">{player.birth_year}</span>
        </div>
        <div className="player-info">
          <p>ðŸ† {player.team || 'N/A'}</p>
          <p>âš½ {player.general_role}</p>
          <p>ðŸ‘Ÿ {player.preferred_foot}</p>
          {player.market_value && <p>ðŸ’° {player.market_value}</p>}
        </div>
        <div className="player-ratings">
          <div>Attuale: {'â­'.repeat(player.current_value || 0)}</div>
          <div>Potenziale: {'â­'.repeat(player.potential_value || 0)}</div>
        </div>
        <div className="player-actions">
          <button onClick={(e) => {
            e.stopPropagation();
            setShowAddForm(player);
          }}>âœï¸</button>
          <button onClick={(e) => {
            e.stopPropagation();
            deletePlayer(player.id);
          }}>ðŸ—‘ï¸</button>
        </div>
      </div>
    );
  }

  // Rendering principale
  return (
    <div className="app">
      <Toaster position="top-right" toastOptions={{
        success: {
          style: {
            background: '#10b981',
            color: 'white',
          },
        },
        error: {
          style: {
            background: '#ef4444',
            color: 'white',
          },
        },
      }} />

      {/* Header */}
      <header className="app-header">
        <h1>âš½ Scouting Database Pro</h1>
        <p>Sistema Professionale con Supabase + Transfermarkt API</p>
      </header>

      {/* Controlli */}
      <div className="controls">
        <input
          type="text"
          placeholder="ðŸ” Cerca per nome o squadra..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />

        <select 
          value={filterRole} 
          onChange={(e) => setFilterRole(e.target.value)}
          className="filter-select"
        >
          <option value="">Tutti i ruoli</option>
          <option value="Portiere">Portiere</option>
          <option value="Difensore">Difensore</option>
          <option value="Terzino">Terzino</option>
          <option value="Centrocampo">Centrocampo</option>
          <option value="Ala">Ala</option>
          <option value="Attaccante">Attaccante</option>
        </select>

        <button onClick={fetchPlayers} className="btn-refresh">
          ðŸ”„ Aggiorna
        </button>

        <button onClick={() => setShowAddForm(true)} className="btn-add">
          âž• Nuovo Giocatore
        </button>
      </div>

      {/* Stato Database */}
      <div className="db-status">
        {!SUPABASE_URL || SUPABASE_URL.includes('tuoprogetto') ? (
          <div className="warning">
            âš ï¸ Database non configurato! Aggiungi le credenziali nel file .env
          </div>
        ) : (
          <div className="success">
            âœ… Connesso a Supabase Database | ðŸŒ Transfermarkt API Attiva
          </div>
        )}
      </div>

      {/* Lista Giocatori */}
      {loading ? (
        <div className="loading">
          <i className="fas fa-spinner fa-spin" style={{ fontSize: '3rem', marginBottom: '1rem' }}></i>
          <p>Caricamento giocatori...</p>
        </div>
      ) : (
        <div className="players-grid">
          {players.length === 0 ? (
            <div className="empty-state">
              <i className="fas fa-users" style={{ fontSize: '4rem', color: '#ccc', marginBottom: '1rem' }}></i>
              <p>Nessun giocatore trovato</p>
              <button onClick={() => setShowAddForm(true)}>
                Aggiungi il primo giocatore
              </button>
            </div>
          ) : (
            players.map(player => (
              <PlayerCard key={player.id} player={player} />
            ))
          )}
        </div>
      )}

      {/* Modal Form */}
      {showAddForm && (
        <PlayerForm
          player={typeof showAddForm === 'object' ? showAddForm : null}
          onSave={addPlayer}
          onClose={() => setShowAddForm(false)}
        />
      )}

      {/* Modal Dettaglio */}
      {selectedPlayer && (
        <div className="modal-overlay" onClick={() => setSelectedPlayer(null)}>
          <div className="modal-content detail" onClick={e => e.stopPropagation()}>
            <button className="close-btn" onClick={() => setSelectedPlayer(null)}>Ã—</button>
            <h2>{selectedPlayer.name}</h2>

            <div className="detail-grid">
              <div><strong>Anno:</strong> {selectedPlayer.birth_year}</div>
              <div><strong>Squadra:</strong> {selectedPlayer.team}</div>
              <div><strong>Ruolo:</strong> {selectedPlayer.general_role}</div>
              <div><strong>Piede:</strong> {selectedPlayer.preferred_foot}</div>
              {selectedPlayer.height && <div><strong>Altezza:</strong> {selectedPlayer.height}</div>}
              {selectedPlayer.market_value && <div><strong>Valore:</strong> {selectedPlayer.market_value}</div>}
              {selectedPlayer.priority && <div><strong>PrioritÃ :</strong> {selectedPlayer.priority}</div>}
              <div><strong>Check:</strong> {selectedPlayer.check_type}</div>
            </div>

            {selectedPlayer.strong_points && (
              <div className="notes-section" style={{ background: '#d1fae5', borderLeft: '4px solid #10b981' }}>
                <h3>âœ… Punti di Forza:</h3>
                <p>{selectedPlayer.strong_points}</p>
              </div>
            )}

            {selectedPlayer.weak_points && (
              <div className="notes-section" style={{ background: '#fee2e2', borderLeft: '4px solid #ef4444' }}>
                <h3>âŒ Punti Deboli:</h3>
                <p>{selectedPlayer.weak_points}</p>
              </div>
            )}

            {selectedPlayer.notes && (
              <div className="notes-section">
                <h3>ðŸ“ Note Scout:</h3>
                <p>{selectedPlayer.notes}</p>
              </div>
            )}

            {selectedPlayer.transfermarkt_link && (
              <div style={{ marginTop: '1rem', textAlign: 'center' }}>
                <a 
                  href={selectedPlayer.transfermarkt_link} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="btn-secondary"
                  style={{ display: 'inline-block', textDecoration: 'none' }}
                >
                  ðŸ”— Vedi su Transfermarkt
                </a>
              </div>
            )}

            <div className="detail-actions">
              <button onClick={() => {
                setShowAddForm(selectedPlayer);
                setSelectedPlayer(null);
              }}>âœï¸ Modifica</button>
              <button onClick={() => deletePlayer(selectedPlayer.id)}>ðŸ—‘ï¸ Elimina</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

